package io.github.team_lodestar.transcendeum;

import io.github.team_lodestar.transcendeum.block.mixcode.MixCodeBlockRegister;
import io.github.team_lodestar.transcendeum.entity.mixcode.MixCodeEntityType;
import io.github.team_lodestar.transcendeum.entity.mixcode.custom.PurifyPowderRenderer;
import io.github.team_lodestar.transcendeum.item.mixcode.MixCodeItemRegister;
import io.github.team_lodestar.transcendeum.world.structure.mixcode.MixCodeStructureRegister;
import net.minecraft.client.Minecraft;
import net.minecraft.client.renderer.ItemRenderer;
import net.minecraft.client.renderer.entity.SpriteRenderer;
import net.minecraftforge.api.distmarker.Dist;
import net.minecraftforge.fml.DistExecutor;
import net.minecraftforge.fml.client.registry.RenderingRegistry;
import org.apache.logging.log4j.Logger;
import org.apache.logging.log4j.LogManager;

import net.minecraftforge.fml.network.simple.SimpleChannel;
import net.minecraftforge.fml.network.NetworkRegistry;
import net.minecraftforge.fml.javafmlmod.FMLJavaModLoadingContext;
import net.minecraftforge.fml.event.server.FMLServerStartingEvent;
import net.minecraftforge.fml.event.lifecycle.FMLCommonSetupEvent;
import net.minecraftforge.fml.event.lifecycle.FMLClientSetupEvent;
import net.minecraftforge.fml.common.Mod;
import net.minecraftforge.eventbus.api.SubscribeEvent;
import net.minecraftforge.event.RegistryEvent;
import net.minecraftforge.common.MinecraftForge;

import net.minecraft.util.ResourceLocation;
import net.minecraft.item.Item;
import net.minecraft.entity.EntityType;
import net.minecraft.enchantment.Enchantment;
import net.minecraft.block.Block;

import java.util.function.Supplier;

@Mod("the_transcendeum")
public class TheTranscendeumMod {
	public static final Logger LOGGER = LogManager.getLogger(TheTranscendeumMod.class);
	private static final String PROTOCOL_VERSION = "1";
	public static final SimpleChannel PACKET_HANDLER = NetworkRegistry.newSimpleChannel(new ResourceLocation("the_transcendeum", "the_transcendeum"),
			() -> PROTOCOL_VERSION, PROTOCOL_VERSION::equals, PROTOCOL_VERSION::equals);
	public TheTranscendeumModElements elements;
	public static final String MODID = "the_transcendeum";

	public TheTranscendeumMod() {
		elements = new TheTranscendeumModElements();
		FMLJavaModLoadingContext.get().getModEventBus().register(this);
		FMLJavaModLoadingContext.get().getModEventBus().addListener(this::init);
		FMLJavaModLoadingContext.get().getModEventBus().addListener(this::clientLoad);
		MinecraftForge.EVENT_BUS.register(new TheTranscendeumModFMLBusEvents(this));
		MixCodeItemRegister.init(FMLJavaModLoadingContext.get().getModEventBus());
		MixCodeBlockRegister.init(FMLJavaModLoadingContext.get().getModEventBus());
		MixCodeEntityType.init(FMLJavaModLoadingContext.get().getModEventBus());
		MixCodeStructureRegister.init(FMLJavaModLoadingContext.get().getModEventBus());
	}

	private void init(FMLCommonSetupEvent event) {
		elements.getElements().forEach(element -> element.init(event));
		event.enqueueWork(() -> {
			MixCodeStructureRegister.setupStructures();
		});
	}

	public void clientLoad(FMLClientSetupEvent event) {
		elements.getElements().forEach(element -> element.clientLoad(event));
		DistExecutor.runWhenOn(Dist.CLIENT, () -> PurifyPowderRenderer::registerEntityRenders);
	}

	@SubscribeEvent
	public void registerBlocks(RegistryEvent.Register<Block> event) {
		event.getRegistry().registerAll(elements.getBlocks().stream().map(Supplier::get).toArray(Block[]::new));
	}

	@SubscribeEvent
	public void registerItems(RegistryEvent.Register<Item> event) {
		event.getRegistry().registerAll(elements.getItems().stream().map(Supplier::get).toArray(Item[]::new));
	}

	@SubscribeEvent
	public void registerEntities(RegistryEvent.Register<EntityType<?>> event) {
		event.getRegistry().registerAll(elements.getEntities().stream().map(Supplier::get).toArray(EntityType[]::new));
	}

	@SubscribeEvent
	public void registerEnchantments(RegistryEvent.Register<Enchantment> event) {
		event.getRegistry().registerAll(elements.getEnchantments().stream().map(Supplier::get).toArray(Enchantment[]::new));
	}

	@SubscribeEvent
	public void registerSounds(RegistryEvent.Register<net.minecraft.util.SoundEvent> event) {
		elements.registerSounds(event);
	}

	private static class TheTranscendeumModFMLBusEvents {
		private final TheTranscendeumMod parent;

		TheTranscendeumModFMLBusEvents(TheTranscendeumMod parent) {
			this.parent = parent;
		}

		@SubscribeEvent
		public void serverLoad(FMLServerStartingEvent event) {
			this.parent.elements.getElements().forEach(element -> element.serverLoad(event));
		}
	}
}
